// Validate HMAC signature
const crypto = require('crypto');
const webhookSecret = $env.WEBHOOK_SECRET || 'n8n-webhook-hmac-secret-change-me';
const body = JSON.stringify($input.first().json);
const signature = $input.first().headers?.['x-webhook-signature'] || '';
const expected = crypto.createHmac('sha256', webhookSecret).update(body).digest('hex');

if (signature !== expected) {
  throw new Error('Invalid webhook signature');
}

return $input.all();

const data = $input.first().json;
const sourceUrl = data.options?.sourceUrl;
const jobId = data.jobId;
const objectId = data.objectId;
const outputDir = '/media';

if (!sourceUrl) {
  return [{ json: { jobId, objectId, status: 'failed', error: 'No sourceUrl provided in options' } }];
}

const uuid = jobId.replace(/-/g, '').substring(0, 12);

return [{ json: { jobId, objectId, sourceUrl, outputDir, uuid } }];
